</div>

<style>
    :root {
        --length: 7em;
    }

    .container {
        max-width: 60em;
        margin: 3em auto
    }

    .l-fractal {
        height: 20em;
        float: left;
        shape-outside: polygon(0% 0%, 25% 0%, 25% 0%, 12.5% 0%, 12.5% 25%, 0% 25%, 0% 50%, 50% 50%, 50% 75%, 37.5% 75%, 37.5% 100%, 0% 50%);
        shape-margin: 1em;
    }

    .greeting {
        padding-top: 0.01em;
        display: grid;
        grid-template-areas: 
            ""
            ""
            "";
        grid-template-columns: var(--length) 0.1em var(--length) var(--length);
        grid-template-rows: var(--length) 0.1em var(--length) var(--length) 0.1em;
        transform: rotate(-45deg);
    }

    .hello {
        grid-column: 3 / 5;
        grid-row: 1;
        align-self: end;
    }

    .name {
        grid-column: 3 / 5;
        grid-row: 3 / 5 ;
        align-self: center;
        font-size: 3rem;
        margin: 1em
    }

    .line {
        background-color: black;
    }

    .line1 {
        grid-column: 3;
        grid-row: 2;
    }


    .line2 {
        grid-column: 2;
        grid-row: 3 / 5;
    }

    .line3 {
        grid-column: 1;
        grid-row: 5;
    }

    .r-fractal {
        height: 20em;
        float: right;
        transform: scaleX(-1);
    }

    .troels {
        height: 20em;
        float: right;
        shape-outside: polygon(25% 0%, 100% 50%, 0% 80%, 0% 45%);
    }
</style> 